import {Image, Text, TouchableOpacity, View} from "react-native";
import React from "react";
import {Icon} from "@rneui/themed";

export const TabButton =({currentTab, setCurrentTab, text, icon} )=>{
    return (

        <TouchableOpacity onPress={() => {
            if (text == "LogOut") {
                // Do your Stuff...
            } else {
                setCurrentTab(text)
            }
        }}>
            <View style={{
                flexDirection: "row",
                alignItems: 'center',
                paddingVertical: 8,
                backgroundColor: currentTab == text ? 'white' : 'transparent',
                paddingLeft: 13,
                paddingRight: 35,
                borderRadius: 8,
                marginTop: 15
            }}>

                {/*<Image source={icon} style={{
                    width: 25, height: 25,
                    tintColor: currentTab == text ? "#5359D1" : "white"
                }}></Image>*/}
                <Icon
                    name={icon} color={currentTab == text ? "#5359D1" : "white"} />

                <Text style={{
                    fontSize: 15,
                    fontWeight: 'bold',
                    paddingLeft: 15,
                    color: currentTab == text ? "#5359D1" : "white"
                }}>{text}</Text>

            </View>
        </TouchableOpacity>
    );
}
